<h2> Cidades </h2>
<table class="table" *ngIf ="tabelacid">
  <thead>
    <tr>
      <th>#</th>
      <th>Nome</th>

    </tr>
  </thead>
  <tbody>
<tr *ngFor="let cid of cidades; index as i">
  <td>{{i+1}}</td>
  <td>{{cid.cidade}}</td>

  <td><button class ="btn btn-success" (click)="selecionarCidade(i)" >Selecionar</button></td>
</tr>
  </tbody>
</table>
